pragma solidity ^0.8.2;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol"; 
import "./wrapper/ApeVault.sol";
import "./ApeAllowanceModule.sol";
import {VaultAPI} from "./wrapper/BaseWrapper.sol";

contract ApeDistributor is ApeAllowanceModule, Ownable {
	using MerkleProof for bytes32[];
	using SafeERC20 for IERC20;


	// address to approve admins for a circle
	// vault => circle => admin address
	mapping(address => mapping(bytes32 => address)) public vaultApprovals;

	// accepted tokens for given circle
	// circle => grant token => bool
	// mapping(address => mapping(address => bool)) public circleToken;

	// roots following this mapping:
	// circle address => token address => epoch ID => root
	mapping(bytes32 =>mapping(address => mapping(uint256 => bytes32))) public epochRoots;
	mapping(bytes32 =>mapping(address => uint256)) public epochTracking;
	mapping(bytes32 => mapping(address => mapping(uint256 => mapping(uint256 => uint256)))) public epochClaimBitMap;

	mapping(bytes32 => mapping(address => uint256)) public circleAlloc;

	// checkpoints following this mapping:
	// circle => token => address => checkpoint
	mapping(bytes32 => mapping(address => mapping(address => uint256))) public checkpoints;

	event AdminApproved(address indexed vault, bytes32 indexed circle, address indexed admin);

	event Claimed(bytes32 circle, address token, uint256 epoch, uint256 index, address account, uint256 amount);

	event apeVaultFundsTapped(address indexed apeVault, address yearnVault, uint256 amount);

	function uploadEpochRoot(
		address _vault,
		bytes32 _circle,
		address _token,
		bytes32 _root,
		uint256 _amount,
		uint8 _tapType)
		external {
		require(vaultApprovals[_vault][_circle] == msg.sender || ApeVaultWrapper(_vault).owner() == msg.sender, "Sender cannot upload a root");
		require(address(ApeVaultWrapper(_vault).vault()) == _token, "Vault cannot supply token");
		// require(circleToken[_circle][_token], "Token not accepted");
		_isTapAllowed(_vault, _circle, _token, _amount);
		uint256 epoch = epochTracking[_circle][_token];
		epochRoots[_circle][_token][epoch] = _root;

		epochTracking[_circle][_token]++;
		circleAlloc[_circle][_token] += _amount;
		uint256 beforeBal = IERC20(_token).balanceOf(address(this));
		uint256 sharesRemoved = ApeVaultWrapper(_vault).tap(_amount, _tapType);
		uint256 afterBal = IERC20(_token).balanceOf(address(this));
		require(afterBal - beforeBal == _amount, "Did not receive correct amount of tokens");
		if (sharesRemoved > 0)
			emit apeVaultFundsTapped(_vault, address(ApeVaultWrapper(_vault).vault()), sharesRemoved);
	}

	function updateCircleAdmin(bytes32 _circle, address _admin) external {
		vaultApprovals[msg.sender][_circle] = _admin;
		emit AdminApproved(msg.sender, _circle, _admin);
	}

	function isClaimed(bytes32 _circle, address _token, uint256 _epoch, uint256 _index) public view returns(bool) {
		uint256 wordIndex = _index / 256;
		uint256 bitIndex = _index % 256;
		uint256 word = epochClaimBitMap[_circle][_token][_epoch][wordIndex];
		uint256 bitMask = 1 << bitIndex;
		return word & bitMask == bitMask;
	}

	function _setClaimed(bytes32 _circle, address _token, uint256 _epoch, uint256 _index) internal {
		uint256 wordIndex = _index / 256;
		uint256 bitIndex = _index % 256;
		epochClaimBitMap[_circle][_token][_epoch][wordIndex] |= 1 << bitIndex;
	}

	function claim(bytes32 _circle, address _token, uint256 _epoch, uint256 _index, address _account, uint256 _checkpoint, bool _redeemShares, bytes32[] memory _proof) public {
		require(!isClaimed(_circle, _token, _epoch, _index), "Claimed already");
		bytes32 node = keccak256(abi.encodePacked(_index, _account, _checkpoint));
		require(_proof.verify(epochRoots[_circle][_token][_epoch], node), "Wrong proof");
		uint256 currentCheckpoint = checkpoints[_circle][_token][_account];
		require(_checkpoint > currentCheckpoint, "Given checkpoint not higher than current checkpoint");

		uint256 claimable = _checkpoint - currentCheckpoint;
		require(claimable <= circleAlloc[_circle][_token], "Can't claim more than circle has to give");
		circleAlloc[_circle][_token] -= claimable;
		checkpoints[_circle][_token][_account] = _checkpoint;
		_setClaimed(_circle, _token, _epoch, _index);
		if (_redeemShares && msg.sender == _account)
			VaultAPI(_token).withdraw(claimable, _account);
		else
			IERC20(_token).safeTransfer(_account, claimable);
		emit Claimed(_circle, _token, _epoch, _index, _account, claimable);
	}

	function claimMany(
		bytes32[] calldata _circles,
		address[] calldata _tokensAndAccounts,
		uint256[] calldata _epochsIndexesCheckpoints,
		bool[] calldata _redeemShares,
		bytes32[][] memory _proofs) external {
		for(uint256 i = 0; i < _circles.length; i++) {
			claim(
				_circles[i],
				_tokensAndAccounts[i],
				_epochsIndexesCheckpoints[i],
				_epochsIndexesCheckpoints[i + _epochsIndexesCheckpoints.length / 3],
				_tokensAndAccounts[i + _tokensAndAccounts.length / 2],
				_epochsIndexesCheckpoints[i + _epochsIndexesCheckpoints.length * 2 / 3],
				_redeemShares[i],
				_proofs[i]
				);
		}
	}
}

pragma solidity ^0.8.2;

import "./TimeLock.sol";

contract ApeRegistry is TimeLock {
	address public feeRegistry;
	address public router;
	address public distributor;
	address public factory;
	address public treasury;

	constructor(uint256 _minDelay) TimeLock(_minDelay) {}

	function setFeeRegistry(address _registry) external itself {
		feeRegistry = _registry;
	}

	function setRouter(address _router) external itself {
		router = _router;
	}

	function setDistributor(address _distributor) external itself {
		distributor = _distributor;
	}

	function setFactory(address _factory) external itself {
		factory = _factory;
	}

	function setTreasury(address _treasury) external itself {
		treasury = _treasury;
	}
}

pragma solidity ^0.8.2;

import "./TimeLock.sol";

contract FeeRegistry is TimeLock(0){
	uint256 private constant _staticFee = 100; // 100 | MAX = 10000
	bool public on;

	function activateFee() external itself {
		on = true;
	}

	function shutdownFee() external itself {
		on = false;
	}

	function staticFee() external view returns(uint256) {
		if (!on)
			return 0;
		return _staticFee;
	}

	function getVariableFee(uint256 _yield, uint256 _tapTotal) external returns(uint256 variableFee) {
		if (!on)
			return 0;
		uint256 yieldRatio = _yield * 1000 / _tapTotal;
		uint256 baseFee = 100;
		if (yieldRatio >= 900)
			variableFee = baseFee;        // 1%     @ 90% profit
		else if (yieldRatio >= 800)
			variableFee = baseFee + 25;   // 1.25%  @ 80% profit
		else if (yieldRatio >= 700)
			variableFee = baseFee + 50;   // 1.50%  @ 70% profit
		else if (yieldRatio >= 600)
			variableFee = baseFee + 75;   // 1.75%  @ 60% profit
		else if (yieldRatio >= 500)
			variableFee = baseFee + 100;  // 2.00%  @ 80% profit
		else if (yieldRatio >= 400)
			variableFee = baseFee + 125;  // 2.25%  @ 80% profit
		else if (yieldRatio >= 300)
			variableFee = baseFee + 150;  // 2.50%  @ 80% profit
		else if (yieldRatio >= 200)
			variableFee = baseFee + 175;  // 2.75%  @ 80% profit
		else if (yieldRatio >= 100)
			variableFee = baseFee + 200;  // 3.00%  @ 80% profit
		else
			variableFee = baseFee + 250;  // 3.50%  @ 0% profit
	}
}

pragma solidity ^0.8.2;

import {VaultAPI, BaseWrapper, RegistryAPI} from "./wrapper/BaseWrapper.sol";
import {ApeVaultFactory} from "./wrapper/ApeVaultFactory.sol";
import {ApeVaultWrapper} from "./wrapper/ApeVault.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./TimeLock.sol";

contract ApeRouter is TimeLock {
	using SafeERC20 for IERC20;


	uint256 constant MAX_UINT = type(uint256).max;

	address public yearnRegistry;
	address public apeVaultFactory;

	constructor(address _reg, address _factory, uint256 _minDelay) TimeLock(_minDelay)  {
		yearnRegistry = _reg;
		apeVaultFactory = _factory;
	}

	event DepositInVault(address indexed vault, address token, uint256 amount);
	event WithdrawFromVault(address indexed vault, address token, uint256 amount);

	function delegateDeposit(address _apeVault, address _token, uint256 _amount) external returns(uint256 deposited) {
		VaultAPI vault = VaultAPI(RegistryAPI(yearnRegistry).latestVault(_token));
		require(address(vault) != address(0), "ApeRouter: No vault for token");
		require(ApeVaultFactory(apeVaultFactory).vaultRegistry(_apeVault), "ApeRouter: Vault does not exist");
		require(address(vault) == address(ApeVaultWrapper(_apeVault).vault()), "ApeRouter: yearn Vault not identical");

        IERC20(_token).safeTransferFrom(msg.sender, address(this), _amount);

		if (IERC20(_token).allowance(address(this), address(vault)) < _amount) {
            IERC20(_token).safeApprove(address(vault), 0); // Avoid issues with some IERC20(_token)s requiring 0
            IERC20(_token).safeApprove(address(vault), MAX_UINT); // Vaults are trusted
        }

		uint256 beforeBal = IERC20(_token).balanceOf(address(this));

		uint256 sharesMinted = vault.deposit(_amount, _apeVault);

        uint256 afterBal = IERC20(_token).balanceOf(address(this));
        deposited = beforeBal - afterBal;
        // `receiver` now has shares of `_bestVault` as balance, converted to `token` here
        // Issue a refund if not everything was deposited

		// adding protocol removal call
        //if (afterBal > 0) IERC20(_token).safeTransfer(msg.sender, afterBal);


		ApeVaultWrapper(_apeVault).addFunds(deposited);
		emit DepositInVault(_apeVault, _token, sharesMinted);
	}

	function delegateWithdrawal(address _recipient, address _apeVault, address _token, uint256 _shareAmount, bool _underlying) external{
		VaultAPI vault = VaultAPI(RegistryAPI(yearnRegistry).latestVault(_token));
		require(address(vault) != address(0), "ApeRouter: No vault for token");
		require(ApeVaultFactory(apeVaultFactory).vaultRegistry(msg.sender), "ApeRouter: Vault does not exist");
		require(address(vault) == address(ApeVaultWrapper(_apeVault).vault()), "ApeRouter: yearn Vault not identical");

		if (_underlying)
			vault.withdraw(_shareAmount, _recipient);
		else
			vault.transfer(_recipient, _shareAmount);
		emit WithdrawFromVault(address(vault), vault.token(), _shareAmount);
	}

	function removeTokens(address _token) external onlyOwner {
		IERC20(_token).transfer(msg.sender, IERC20(_token).balanceOf(address(this)));
	}

	 /**
     * @notice
     *  Used to update the yearn registry.
     * @param _registry The new _registry address.
     */
    function setRegistry(address _registry) external itself {
        yearnRegistry = _registry;
    }
}

pragma solidity ^0.8.2;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {Math} from "@openzeppelin/contracts/utils/math/Math.sol";
import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";

struct StrategyParams {
    uint256 performanceFee;
    uint256 activation;
    uint256 debtRatio;
    uint256 minDebtPerHarvest;
    uint256 maxDebtPerHarvest;
    uint256 lastReport;
    uint256 totalDebt;
    uint256 totalGain;
    uint256 totalLoss;
}

interface VaultAPI is IERC20 {
    function name() external view returns (string calldata);

    function symbol() external view returns (string calldata);

    function decimals() external view returns (uint256);

    function apiVersion() external pure returns (string memory);

    function permit(
        address owner,
        address spender,
        uint256 amount,
        uint256 expiry,
        bytes calldata signature
    ) external returns (bool);

    // NOTE: Vyper produces multiple signatures for a given function with "default" args
    function deposit() external returns (uint256);

    function deposit(uint256 amount) external returns (uint256);

