pragma solidity ^0.8.2;

import "@openzeppelin/contracts/access/Ownable.sol";

contract TokenAccessControl is Ownable {
	mapping(address => bool) public minters;
	mapping(address => bool) public whitelistedAddresses;

	bool public paused;
	bool public foreverUnpaused;
	bool public mintingDisabled;
	bool public whitelistDisabled;

	event MintersAdded(address[] minters);
	event MintersRemoved(address[] minters);
	event WhitelistedAddressesAdded(address[] minters);
	event WhitelistedAddressesRemoved(address[] minters);


	modifier isPaused() {
		require(!paused || (whitelistedAddresses[msg.sender] && !whitelistDisabled), "AccessControl: User cannot transfer");
		_;
	}

	modifier isMinter(address _caller) {
		require(!mintingDisabled, "AccessControl: Contract cannot mint tokens anymore");
		require(minters[_caller], "AccessControl: Cannot mint");
		_;
	}

	function disableWhitelist() external onlyOwner {
		require(!whitelistDisabled, "AccessControl: Whitelist already disabled");
		whitelistDisabled = true;
	}

	function changePauseStatus(bool _status) external onlyOwner {
		require(!foreverUnpaused, "AccessControl: Contract is unpaused forever");
		paused = _status;
	} 


	function disablePausingForever() external onlyOwner {
		require(!foreverUnpaused, "AccessControl: Contract is unpaused forever");
		foreverUnpaused = true;
		paused = false;
	}

	function addMinters(address[] calldata _minters) external onlyOwner {
		require(!mintingDisabled, "AccessControl: Contract cannot mint tokens anymore");

		for(uint256 i = 0; i < _minters.length; i++)
			minters[_minters[i]] = true;
		emit MintersAdded(_minters);
	}

	function removeMinters(address[] calldata _minters) external onlyOwner {
		require(!mintingDisabled, "AccessControl: Contract cannot mint tokens anymore");

		for(uint256 i = 0; i < _minters.length; i++)
			minters[_minters[i]] = false;
		emit MintersRemoved(_minters);
	}

	function addWhitelistedAddresses(address[] calldata _addresses) external onlyOwner {
		require(!whitelistDisabled, "AccessControl: Whitelist already disabled");

		for(uint256 i = 0; i < _addresses.length; i++)
			whitelistedAddresses[_addresses[i]] = true;
		emit WhitelistedAddressesAdded(_addresses);
	}

	function removeWhitelistedAddresses(address[] calldata _addresses) external onlyOwner {
		require(!whitelistDisabled, "AccessControl: Whitelist already disabled");

		for(uint256 i = 0; i < _addresses.length; i++)
			whitelistedAddresses[_addresses[i]] = false;
		emit WhitelistedAddressesRemoved(_addresses);
	}

	function disableMintingForever() external onlyOwner {
		require(!mintingDisabled, "AccessControl: Contract cannot mint anymore");
		mintingDisabled = true;
	}
}

pragma solidity ^0.8.2;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract MockRegistry {

	address public governance;

	mapping(address => address) public latestVault;
	mapping(address => uint256) public numVaults;
	mapping(address => mapping(uint256 => address)) public vaults;

	constructor() {
		governance = msg.sender;
	}

	function addVault(address _token, address _vault) external {
		uint256 current = numVaults[_token];
		latestVault[_token] = _vault;
		numVaults[_token]++;
		vaults[_token][current] = _vault;
	}
}

contract MockVaultFactory {
	MockRegistry public registry;

	constructor(address _reg) {
		registry = MockRegistry(_reg);
	}

	function createVault(address _token) external returns(address) {
		MockVault newVault = new MockVault(_token);
		registry.addVault(_token, address(newVault));
		return address(newVault);
	}
}

contract MockVault is ERC20("VaultToken", "VT") {

	MockToken public token;

	uint256 public depositLimit;

	constructor (address _token) {
		token = MockToken(_token);
		depositLimit = type(uint256).max;
	}

	function totalAssets() external view returns (uint256) {
		return token.balanceOf(address(this));
	}

	function pricePerShare() public view returns(uint256) {
		if (totalSupply() == 0)
			return 10 ** uint256(decimals());
		else {
			return (10 ** uint256(decimals())) * token.balanceOf(address(this)) / totalSupply();
		}
	}

	function maxAvailableShares() external view returns (uint256) {
		return totalSupply();
	}

	function deposit() external returns (uint256) {
		return deposit(token.balanceOf(msg.sender));
	}

    function deposit(uint256 amount) public returns (uint256) {
		return deposit(amount, msg.sender);
	}

    function deposit(uint256 amount, address recipient) public returns (uint256 deposited) {
		depositLimit -= amount;
		if (totalSupply() == 0) {
			_mint(recipient, amount);
			deposited = amount;
		}
		else {
			uint256 _amount = amount * balanceOf(address(this)) / token.balanceOf(address(this));
			_mint(recipient, _amount);
			deposited = _amount;
		}
		token.transferFrom(msg.sender, address(this), amount);
	}

    // NOTE: Vyper produces multiple signatures for a given function with "default" args
    function withdraw() external returns (uint256) {
		return withdraw(balanceOf(msg.sender));
	}

    function withdraw(uint256 maxShares) public returns (uint256) {
		return withdraw(maxShares, msg.sender);
	}

    function withdraw(uint256 maxShares, address recipient) public returns (uint256 amount) {
		require(maxShares <= balanceOf(msg.sender) && maxShares > 0);
		amount = maxShares * token.balanceOf(address(this)) / totalSupply();
		_burn(msg.sender, maxShares);
		token.transfer(recipient, amount);
	}

	function goodHarvest(uint256 _apr) external {
		uint256 toMint = token.balanceOf(address(this)) * _apr / 100;
		token.mint(toMint);
	}

	function badHarvest(uint256 _apr) external {
		uint256 toBurn = token.balanceOf(address(this)) * _apr / 100;
		token.burn(toBurn);
	}
}

contract MockToken is ERC20("Mock", "MCK") {
	function mint(uint256 _amount) external {
		_mint(msg.sender, _amount);
	}

	function burn(uint256 _amount) external {
		_burn(msg.sender, _amount);
	}
}
